import React, { useState } from "react";

function App() {
    const [selectedFile, setSelectedFile] = useState(null);
    const [docType, setDocType] = useState("travel ticket");
    const [output, setOutput] = useState(null);

    const uploadFile = async () => {
        if (!selectedFile) {
            alert("Please select a file first!");
            return;
        }

        let formData = new FormData();
        formData.append("file", selectedFile);
        formData.append("documentType", docType);

        try {
            let response = await fetch("http://localhost:5000/uploads", {
                method: "POST",
                body: formData
            });
            let result = await response.json();
            setOutput(result);
        } catch (error) {
            console.error("Upload Error:", error);
            alert("Failed to upload file! BITCH");
        }
    };

    return (
        <div style={{ padding: "20px" }}>
            <h1>Upload PDF for Parsing</h1>
            <input type="file" onChange={(e) => setSelectedFile(e.target.files[0])} />
            <select value={docType} onChange={(e) => setDocType(e.target.value)}>
                <option value="travel ticket">Travel Ticket</option>
                <option value="invoice">Invoice</option>
            </select>
            <button onClick={uploadFile}>Upload</button>
            {output && <pre>{JSON.stringify(output, null, 2)}</pre>}
        </div>
    );
}

export default App;
